	public Tweet crearTweet(JSONObject json) {
		String tweetStr = null, id = null, dateStr, userStr = null;
		SimpleDateFormat format = new SimpleDateFormat(
				"EEE MMM dd HH:mm:ss Z yyyy");
		Date date = null;

		try {
			tweetStr = json.getString("text");

			dateStr = json.getString("created_at");

			userStr = json.getJSONObject("user").getString("name");

			id = json.getJSONObject("user").getString("id");

			date = format.parse(dateStr);

		} catch (Exception e) {
			e.printStackTrace();
		}
		return new Tweet(new User(id, userStr), tweetStr, date);
	}

